                                  icon="chevron-right"
                                  className="ml-2"
                                  size="sm"
                                ></MDBIcon>
                              </div>
                            </a>
                          </MDBCardBody>
                          <div className="time3-index">
                            <MDBIcon far icon="clock" />
                            <span>
                              {" "}
                              {moment(
                                new Date(element.date.seconds * 1000)
                              ).format("lll")}
                            </span>
                          </div>
                        </MDBCard>
