          </div>
        </div>

        <div className="box-index">
          <div className="headother-index">โกงผ่านช่องทางอื่นๆ ล่าสุด</div>
          <div className="otherbox-index">
            <div className="row">
              {WebsiteCount
                ? WebsiteCount.map((element, index) => {
                    return (
                      <div className="column4-index" key={index}>
