import React, { useContext, useState, useMemo } from "react";
import {
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarNav,
  MDBNavItem,
  MDBNavbarToggler,
  MDBCollapse,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
} from "mdbreact";
import { BrowserRouter as Router, useHistory } from "react-router-dom";
import "./navnew.css";
import { auth } from "../Frontfirebase";
import usercontext from "../context/usercontext";
import axios from "axios";
import * as moment from "moment";
import "moment/locale/th";
import { Nav } from "react-bootstrap";
const NavbarPage = ({ SetshowDropdown, showDropdown }) => {
  var { user } = useContext(usercontext);
  const [displayname, setDisplayname] = useState();
  const [admin, setAdmin] = useState(false);
  const [loading, setLoading] = useState(true);
  const [isOpen, setIsopen] = useState(false);
  const [search, Setsearch] = useState();
  const [searching, Setsearching] = useState();
  const [lastsearch, Setlastsearch] = useState();
  const [refresh, Setrefresh] = useState();
  const [allpost, Setallpost] = useState();
  const [countNoti, setCountNoti] = useState([]);
  const [noti, setNoti] = useState([]);
  const [haha, Sethaha] = useState();
  const [error, Seterror] = useState();
  const [hideCountNoti, SetHideCountNoti] = useState(false);
  const [hideCountNotiAlways, SetHideCountNotiAlways] = useState(false);
  let history = useHistory();
  let i = 0; //forsearch

  const logout = () => {
    auth
      .signOut()
      .then(() => {
        console.log("Signout");
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const toggleCollapse = () => {
    setIsopen(!isOpen);
  };
  const notiChangeClick = async () => {
    if (countNoti.length != 0) {
      SetHideCountNoti(true);
      await axios.post(
        `https://monkeyfruad01.herokuapp.com/post/notichangeclick/${user.uid}`,
        { countNoti }
      );
    }
  };
  const notiChangeRead = async (notiId) => {
    await axios.post(
      `https://monkeyfruad01.herokuapp.com/post/notificationread/${notiId}`
    );
  };
  const handlesearch = () => {
    try {
      if (search) {
        const getdata = allpost.filter((doc) => {
          return (
            doc.name.toLowerCase().includes(search.toLowerCase()) ||
            doc.surname.toLowerCase().includes(search.toLowerCase()) ||
            doc.accountnumber.includes(search) ||
            (doc.name.toLowerCase() + " " + doc.surname.toLowerCase()).includes(
              search.toLowerCase()
            )
          );
        });
        Setsearch("");
        if (getdata) {
          console.log(getdata);
          history.push({
            pathname: "/entersearch",
            search: "?are you ok",
            state: {
              getdata,
              search,
            },
          });
        }
      }
    } catch (err) {
      console.log(err);
    }
  };
  const adminhandlesearch = () => {
    try {
      if (search) {
        const getdata = allpost.filter((doc) => {
          return (
            doc.name.toLowerCase().includes(search.toLowerCase()) ||
            doc.surname.toLowerCase().includes(search.toLowerCase()) ||
            doc.accountnumber.includes(search) ||
            (doc.name.toLowerCase() + " " + doc.surname.toLowerCase()).includes(
              search.toLowerCase()
            )
          );
        });
        Setsearch("");
        if (getdata) {
          console.log(getdata);
          history.push({
            pathname: "/adminentersearch",
            search: "?are you ok",
            state: {
              getdata,
              search,
            },
          });
        }
      }
    } catch (err) {
      console.log(err);
    }
  };

  const initSearch = async () => {
    try {
      const getallthief = await axios.get(
        `https://monkeyfruad01.herokuapp.com/thief/thief`
      );
      Setsearching(getallthief.data.item);
      const getallpost = await axios.get(
        `https://monkeyfruad01.herokuapp.com/post/post`
      );
      Setallpost(getallpost.data.item);
      const getthief = getallthief.data.item;
      if (search) {
        Seterror();
        Setlastsearch(
          getthief.filter((doc) => {
            if (
              (
                doc.name.toLowerCase() +
                " " +
                doc.surname.toLowerCase()
              ).startsWith(search.toLowerCase())
            ) {
              Sethaha(true);
            }
            if (doc.accountnumber.startsWith(search)) {
              Sethaha(true);
            }
            if (doc.name.toLowerCase().startsWith(search.toLowerCase())) {
              Sethaha(true);
            }
            if (doc.surname.toLowerCase().startsWith(search.toLowerCase())) {
              Sethaha(true);
            }
            return (
              doc.name.toLowerCase().startsWith(search.toLowerCase()) ||
              doc.surname.toLowerCase().startsWith(search.toLowerCase()) ||
              doc.accountnumber.startsWith(search) ||
              (
                doc.name.toLowerCase() +
                " " +
                doc.surname.toLowerCase()
              ).startsWith(search.toLowerCase())
            );
          })
        );
      }
      if (!search) {
        Setlastsearch();
      }
    } catch (err) {
      console.log(err);
    }
  };
  const initnoti = async () => {
    axios
      .post(`https://monkeyfruad01.herokuapp.com/post/getnotification/${user.uid}`)
      .then((result) => {
        setNoti(result.data);
      })
      .catch((err) => {
        console.log(err);
      });
    await axios
      .post(
        `https://monkeyfruad01.herokuapp.com/post/getnoticlickfalse/${user.uid}`
      )
      .then((result) => {
        if (result.data[0] === undefined) {
          SetHideCountNotiAlways(true);
        } else {
          setCountNoti(result.data);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const initUser = async () => {
    await axios
      .post("https://monkeyfruad01.herokuapp.com/user/session", {
        user: user,
      })
      .then((result) => {
        if (result.data.data.role === "admin") {
          setAdmin(true);
        }
        setDisplayname(result.data.data.username);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  useMemo(async () => {
    if (user) {
      initUser();
      await initnoti();
    }
    await initSearch();
    setLoading(false);
  }, [user, search, hideCountNoti,]);
  return loading ? (
    ""
  ) : admin ? (
    <Router>
      <MDBNavbar light expand="lg" className="navbarnew navbar-expand-lg">
        <MDBNavbarBrand href="/">
          <img src="/img/logo-mf.png" className="logo-nav" />
        </MDBNavbarBrand>
        <MDBNavbarToggler onClick={toggleCollapse} />
        <MDBCollapse id="navbarCollapse3" isOpen={isOpen} navbar>
          <MDBNavbarNav left className="center-nav">
            <MDBNavItem>
              <Nav.Link href="/">  </Nav.Link>
            </MDBNavItem>
            <MDBNavItem>
              <Nav.Link href="/report"></Nav.Link>
            </MDBNavItem>
          </MDBNavbarNav>
          <MDBNavbarNav right>
            <MDBNavItem>
              <div className=" my-0">
                <input
                  className="box-nav mr-sm-2"
                  type="text"
                  placeholder=""
                  aria-label="Search"
                  value={search}
                  onChange={(e) => {
                    Setsearch(e.target.value);
                    SetshowDropdown(true);
                  }}
                />
              </div>
            </MDBNavItem>

            <button onClick={() => adminhandlesearch()} className="button-nav">
              
            </button>
            <MDBNavItem>
              <Nav.Link onClick={logout} href="/login">
                
              </Nav.Link>
            </MDBNavItem>
          </MDBNavbarNav>
        </MDBCollapse>
      </MDBNavbar>
      <div className="ggadmin">
        {lastsearch
          ? lastsearch.map((doc) => {
            let thiefNameAndSurname = `${doc.name} ${doc.surname}`;
              console.log(thiefNameAndSurname)
              i++;
              return (
                <div className="boxsearch-nav">
                  {i <= 10 ? (
                    <div>
                      {" "}
                      {haha ? (
                        showDropdown ? (
                          <button
                            className="search-nav"
                            onClick={() => (
                              history.push({
                                pathname: `/admin/thief/post/${thiefNameAndSurname}`,
                                search: "?are you ok",
                              }),
                              window.location.reload(true)
                            )}
                          >
                            <div>
                              {" "}
                              {doc.name} {doc.surname} {doc.accountnumber}
                            </div>
                          </button>
                        ) : null
                      ) : null}
                    </div>
                  ) : null}
                </div>
              );
            })
          : null}
        {lastsearch ? (
          showDropdown ? (
            <div className="dropsearch-nav" onClick={() => adminhandlesearch()}>
               {search}
            </div>
          ) : null
        ) : null}
      </div>
    </Router>
  ) : (
    <Router>
      <MDBNavbar light expand="md" className="navbarnew">
        <Nav.Link href="/">
          <img src="/img/logo-mf.png" className="logo-nav" />
        </Nav.Link>
        {user ? (
          <MDBDropdown>
            <MDBContainer className="bigslide">
          <MDBBtn
            className="btnslide"
            color="default-color"
            onClick={() => toggle()}
          >
            <i class="fa fa-filter"></i>
          </MDBBtn>
          <MDBModal
            isOpen={isOpen}
            toggle={() => toggle()}
            fullHeight
            position="right"
          >
            <MDBModalHeader className="nameslide" toggle={() => toggle()}>
              
            </MDBModalHeader>
            <MDBModalBody className="biggroup-slide">
              <div className="container-postright2-slide">
                <div className="post-group1-slide">
                   :
                  <select
                    as="select"
                    name="post-groupsorting1-slide"
                    className="post-groupsorting1-slide"
                    onChange={(e) => Setsortvalue(e.target.value)}
                  >
                    <option value=""> </option>
                    <option value="">
                      
                    </option>
                  </select>
                </div>

                <div className="line-postgroup1-slide"></div>
                <div className="post-group2-slide">
                  <div className="post-namegroup1-slide"></div>
                  <Form.Row>
                    <Form.Group>
                      <div class="custom-control custom-checkbox groupcheckbox1-slide">
                        <input
                          type="checkbox"
                          class="custom-control-input groupcheckboxinput1"
                          id="defaultInline1"
                          onChange={(e) => Setfacebook(e.target.value)}
                          onClick={() => Setcheckfacebook(!checkfacebook)}
                        ></input>
                        <label
                          class="custom-control-label groupcheckboxlabel1"
                          for="defaultInline1"
                        >
                          Facebook
                        </label>
                      </div>
                    </Form.Group>
                    <Form.Group>
                      <div class="custom-control custom-checkbox groupcheckbox1-slide">
                        <input
                          type="checkbox"
                          class="custom-control-input groupcheckboxinput1"
                          id="defaultInline2"
                          onChange={(e) => Setline(e.target.value)}
                          onClick={() => Setcheckline(!checkline)}
                        />
                        <label
                          class="custom-control-label groupcheckboxlabel1"
                          for="defaultInline2"
                        >
                          Line
                        </label>
                      </div>
                    </Form.Group>
                  </Form.Row>

                  <Form.Row>
                    <Form.Group>
                      <div class="custom-control custom-checkbox groupcheckbox1-slide">
                        <input
                          type="checkbox"
                          class="custom-control-input groupcheckboxinput1"
                          id="defaultInline3"
                          onChange={(e) => Setinstagram(e.target.value)}
                          onClick={() => Setcheckinstagram(!checkinstagram)}
                        ></input>
                        <label
                          class="custom-control-label groupcheckboxlabel1"
                          for="defaultInline3"
                        >
                          Instagram
                        </label>
                      </div>
                    </Form.Group>
                    <Form.Group>
                      <div class="custom-control custom-checkbox groupcheckbox1-slide">
                        <input
                          type="checkbox"
                          class="custom-control-input groupcheckboxinput1"
                          id="defaultInline4"
                          onChange={(e) => Settwitter(e.target.value)}
                          onClick={() => Setchecktwitter(!checktwitter)}
                        ></input>
                        <label
                          class="custom-control-label groupcheckboxlabel1"
                          for="defaultInline4"
                        >
                          Twitter
                        </label>
                      </div>
                    </Form.Group>
                  </Form.Row>

                  <Form.Row>
                    <Form.Group>
                      <div class="custom-control custom-checkbox groupcheckbox1-slide">
                        <input
                          type="checkbox"
                          class="custom-control-input groupcheckboxinput1"
                          id="defaultInline5"
                          onChange={(e) => Setother(e.target.value)}
                          onClick={() => Setcheckother(!checkother)}
                        ></input>
                        <label
                          class="custom-control-label groupcheckboxlabel1"
                          for="defaultInline5"
                        >
                          
                        </label>
                      </div>
                    </Form.Group>
                  </Form.Row>
                </div>
                <div className="line-postgroup2-slide"></div>
                <div className="post-group3-slide">
                  <div className="post-namegroup2-slide"></div>
                  <div class="custom-control custom-checkbox groupcheckbox2-slide">
                    <input
                      type="checkbox"
                      class="custom-control-input groupcheckboxinput2"
                      id="defaultInline1-2"
                      onChange={(e) => Setcloth(e.target.value)}
                      onClick={() => Setcheckcloth(!checkcloth)}
                    ></input>
                    <label
                      class="custom-control-label groupcheckboxlabel2"
                      for="defaultInline1-2"
                    >
                      
                    </label>
                  </div>
                  <div class="custom-control custom-checkbox groupcheckbox2-slide">
                    <input
                      type="checkbox"
                      class="custom-control-input groupcheckboxinput2"
                      id="defaultInline2-2"
                      onChange={(e) => Setassesory(e.target.value)}
                      onClick={() => Setcheckassesory(!checkassesory)}
                    ></input>
                    <label
                      class="custom-control-label groupcheckboxlabel2"
                      for="defaultInline2-2"
                    >
                      
                    </label>
                  </div>
                  <div class="custom-control custom-checkbox groupcheckbox2-slide">
                    <input
                      type="checkbox"
                      class="custom-control-input groupcheckboxinput2"
                      id="defaultInline3-2"
                      onChange={(e) => Setshoe(e.target.value)}
                      onClick={() => Setcheckshoe(!checkshoe)}
                    ></input>
                    <label
                      class="custom-control-label groupcheckboxlabel2"
                      for="defaultInline3-2"
                    >
                      
                    </label>
                  </div>
                  <div class="custom-control custom-checkbox groupcheckbox2-slide">
                    <input
                      type="checkbox"
                      class="custom-control-input groupcheckboxinput2"
                      id="defaultInline4-2"
                      onChange={(e) => Setbag(e.target.value)}
                      onClick={() => Setcheckbag(!checkbag)}
                    ></input>
                    <label
                      class="custom-control-label groupcheckboxlabel2"
                      for="defaultInline4-2"
                    >
                      
                    </label>
                  </div>
                  <div class="custom-control custom-checkbox groupcheckbox2-slide">
                    <input
                      type="checkbox"
                      class="custom-control-input groupcheckboxinput2"
                      id="defaultInline5-2"
                      onChange={(e) => Setphone(e.target.value)}
                      onClick={() => Setcheckphone(!checkphone)}
                    ></input>
                    <label
                      class="custom-control-label groupcheckboxlabel1"
                      for="defaultInline5-2"
                    >
                      
                    </label>
                  </div>
                  <div class="custom-control custom-checkbox groupcheckbox2-slide">
                    <input
                      type="checkbox"
                      class="custom-control-input groupcheckboxinput2"
                      id="defaultInline6"
                      onChange={(e) => Setfood(e.target.value)}
                      onClick={() => Setcheckfood(!checkfood)}
                    ></input>
                    <label
                      class="custom-control-label groupcheckboxlabel2"
                      for="defaultInline6"
                    >
                      
                    </label>
                  </div>
                  <div class="custom-control custom-checkbox groupcheckbox2-slide">
                    <input
                      type="checkbox"
                      class="custom-control-input groupcheckboxinput2"
                      id="defaultInline7"
                      onChange={(e) => Setfoodwealth(e.target.value)}
                      onClick={() => Setcheckfoodwealth(!checkfoodwealth)}
                    ></input>
                    <label
                      class="custom-control-label groupcheckboxlabel2"
                      for="defaultInline7"
                    >
                      
                    </label>
                  </div>
                  <div class="custom-control custom-checkbox groupcheckbox2-slide">
                    <input
                      type="checkbox"
                      class="custom-control-input groupcheckboxinput2"
                      id="defaultInline8"
                      onChange={(e) => Setbeauty(e.target.value)}
                      onClick={() => Setcheckbeauty(!checkbeauty)}
                    ></input>
                    <label
                      class="custom-control-label groupcheckboxlabel2"
                      for="defaultInline8"
                    >
                      
                    </label>
                  </div>
                  <div class="custom-control custom-checkbox groupcheckbox2-slide">
                    <input
                      type="checkbox"
                      class="custom-control-input groupcheckboxinput2"
                      id="defaultInline9"
                      onChange={(e) => Setcomputer(e.target.value)}
                      onClick={() => Setcheckcomputer(!checkcomputer)}
                    ></input>
                    <label
                      class="custom-control-label groupcheckboxlabel2"
                      for="defaultInline9"
                    >
                      
                    </label>
                  </div>
                  <div class="custom-control custom-checkbox groupcheckbox2-slide">
                    <input
                      type="checkbox"
                      class="custom-control-input groupcheckboxinput2"
                      id="defaultInline10"
                      onChange={(e) => Setcamera(e.target.value)}
                      onClick={() => Setcheckcamera(!checkcamera)}
                    ></input>
                    <label
                      class="custom-control-label groupcheckboxlabel2"
                      for="defaultInline10"
                    >
                      
                    </label>
                  </div>
                  <div class="custom-control custom-checkbox groupcheckbox2-slide">
                    <input
                      type="checkbox"
                      class="custom-control-input groupcheckboxinput2"
                      id="defaultInline11"
                      onChange={(e) => Setsport(e.target.value)}
                      onClick={() => Setchecksport(!checksport)}
                    ></input>
                    <label
                      class="custom-control-label groupcheckboxlabel2"
                      for="defaultInline11"
                    >
                      
                    </label>
                  </div>
                  <div class="custom-control custom-checkbox groupcheckbox2-slide">
                    <input
                      type="checkbox"
                      class="custom-control-input groupcheckboxinput2"
                      id="defaultInline12"
                      onChange={(e) => Setmedia(e.target.value)}
