import React, { useEffect, useState, useContext } from "react";
import { Form, Col, FormControl, Button, Modal } from "react-bootstrap";
import { Link } from "react-router-dom";
import Axios from "axios";
import * as moment from "moment";
import "moment/locale/th";
import Modalimage from "../../user/components/Modalimage";

const Listhidereport = ({ reportelement, CancleClick, DeleteClick }) => {
  const [UsernamePost, SetUsernamePost] = useState("");
  const [Show, setShow] = useState(false);
  const [isActive, setIsActive] = useState(false);
  const [UsernameReport, SetUsernameReport] = useState("");
  const [checkselectOne, setCheckSelectOne] = useState(false);
  const [checkselectTwo, setCheckSelectTwo] = useState(false);
  const [checkselectThree, setCheckSelectThree] = useState(false);
  const [isopen, Setisopen] = useState(false);
  const [imagemodal, Setimagemodal] = useState();

  const [openmodal, Setopenmodal] = useState(false);
  const [modalcommentid, Setmodalcommentid] = useState();
  const [modalcommentmore, Setmodalcommentmore] = useState();
  const [fuck, Setfuck] = useState([]);
  const [imagesFile, setImagesFile] = useState();
  const [files, Setfiles] = useState();
  const [change, SetChange] = useState();
  const { v4: uuidv4 } = require("uuid");
  let uuid = uuidv4();

  const Hiddendropdown = () => {
    SetshowDropdown(false);
  };

  const handlemodalopen = async () => {
    Setopenmodal(true);
  };
  const handlemodalclose = async () => {
    Setopenmodal(false);
  };

  const handledeletetorerender = async () => {
    SetChange(uuid);
  };
  
  const handleShow = (e) => {
    e.preventDefault();
    setShow(true);
  };
  const handleClose = () => {
    setShow(false);
  };
  const handleopenmodal = async () => {
    Setisopen(true);
    handleClose()
  };
  const handleclosemodal = async (e) => {
    Setisopen(false);
    handleShow(e)
  };
  const InitReport = async () => {
    try {
      InitOtherData();
      const usernamepost = await Axios.get(
        `https://monkeyfruad01.herokuapp.com/post/mypost/${reportelement.postid}`
      );
      SetUsernamePost(usernamepost.data.item);
      const usernamereport = await Axios.get(
        `https://monkeyfruad01.herokuapp.com/user/session/${reportelement.userreport}`
      );
      SetUsernameReport(usernamereport.data.item);
    } catch (err) {
      console.log(err);
    }
  };
  const InitOtherData = () => {
    if (reportelement.selectOne === "") {
      setCheckSelectOne(false);
    } else if (reportelement.selectOne != "") {
      setCheckSelectOne(true);
    }
    if (reportelement.selectTwo === "") {
      setCheckSelectTwo(false);
    } else if (reportelement.selectTwo != "") {
      setCheckSelectTwo(true);
    }
    if (reportelement.selectThree === "") {
      setCheckSelectThree(false);
    } else if (reportelement.selectThree != "") {
      setCheckSelectThree(true);
    }
  };
  const cancleHide = async () => {
    console.log("CanCleHide ACtive")
      await Axios.post(
        `https://monkeyfruad01.herokuapp.com/post/report/changeread/${reportelement.uid}`
      );
      CancleClick();
  };
  const deleteReport = async () => {
      await Axios.post(
        `https://monkeyfruad01.herokuapp.com/post/report/delete/${reportelement.uid}`
      );
      DeleteClick();
  };
  useEffect(() => {
    InitReport();
  }, []);
  return (
    <div 
      onClick={() => {
        if (isActive == true) {
          setIsActive(false);
        }
      }}
    >
      <div className="container-report1">
        <div className="container-report2">
          <Form className="formsize-report d-none d-sm-none d-md-inline">
            <div className="container-reportsetiing">
              <div className="menu-containerreportsetting">
                <div
                  onClick={() => setIsActive(!isActive)}
                  className="reportbuttonsetting"
                >
                  <img
                    className="reportimg-setting"
                    src="/img/setting.png"
                    alt="avatar"
                  ></img>
                </div>
                <div
                  className={`reportmenusetting ${
                    isActive ? "active" : "inactive"
                  }`}
                >
                  <ul className="ul-reportmenusetting">
                    <li className="li-reportmenusetting">
                      <a className="a-reportmenusetting" onClick={() => cancleHide()}>
                        <div
                          className="a-reportmenusetting1"
                        >
                          
                        </div>
                      </a>
                    </li>
                    <li className="li-reportmenusetting">
                      <div
                        className="a-reportmenusetting"
                        onClick={() => deleteReport()}
                      >
                        {" "}
                        {" "}
                      </div>
                    </li>
                  </ul>
                </div>
              </div>
            </div>
            <Form.Row>
              <Form.Group
                as={Col}
                className="report-left col-md-6 col-12"
                controlId="formGridName"
              >
                <Form.Label>
                   :{" "}
                  {UsernameReport && UsernameReport[0].username}{" "}
                </Form.Label>
              </Form.Group>

              {checkselectOne ? (
                <Form.Group controlId="formBasicCheckbox">
                  <Form.Check
                    type="checkbox"
                    label=""
                    checked
                    disabled
                  />
                </Form.Group>
              ) : (
                <Form.Group controlId="formBasicCheckbox">
                  <Form.Check
                    type="checkbox"
                    label=""
                    disabled
                  />
                </Form.Group>
              )}
            </Form.Row>

            <Form.Row>
              <Form.Group
                as={Col}
                className="report-left col-md-6 col-12"
                controlId="formGridName"
              >
                <Form.Label>
                   :{" "}
                  {moment(new Date(reportelement.date.seconds * 1000)).format(
                    "MM/DD/YYYY HH:mm"
                  )}{" "}
                </Form.Label>
              </Form.Group>
              {checkselectTwo ? (
                <Form.Group controlId="formBasicCheckbox">
                  <Form.Check
                    type="checkbox"
                    label=""
                    checked
                    disabled
                  />
                </Form.Group>
              ) : (
                <Form.Group controlId="formBasicCheckbox">
                  <Form.Check
                    type="checkbox"
                    label=""
                    disabled
                  />
                </Form.Group>
              )}
            </Form.Row>

            <Form.Row>
              <Form.Group
                as={Col}
                className="report-left col-md-6 col-12"
                controlId="formGridName"
              >
                <Form.Label>
                   : {UsernamePost && UsernamePost[0].username}{" "}
                </Form.Label>
              </Form.Group>

              {checkselectThree ? (
                <Form.Group controlId="formBasicCheckbox">
                  <Form.Check type="checkbox" label="" checked disabled />
                </Form.Group>
              ) : (
                <Form.Group controlId="formBasicCheckbox">
                  <Form.Check type="checkbox" label="" disabled />
                </Form.Group>
              )}
            </Form.Row>

            <Form.Row>
              <Form.Group
                as={Col}
                className="report-left col-md-6 col-12"
                controlId="formGridName"
              >
                <Form.Label>
                   :
                    <div
                      variant="primary"
                      onClick={(e) => handleShow(e)}
                      className="proof-button-reported"
                    >
                      
                  </div>{" "}
                </Form.Label>
                <Form.Row>
                  <Modal
                    show={Show}
                    onHide={handleClose}
                    className="modalreport"
                  >
                    <Modal.Header closeButton>
                      <Modal.Title className="namereport">
                        
                      </Modal.Title>
                    </Modal.Header>
                    <Modal.Body className="bigreport1">
                      <Form.Row>
                        {reportelement.fileUploads
                          ? reportelement.fileUploads.map((element, index) => {
                              return (
                                <div className="img-holder-badslip">
                                
                                    <img
                                      className="img-badreport"
                                      alt=""
                                      src={`${element.url}`}
                                      style={{ overflow: "hidden" }}
                                      onClick={() => (
                                        Setimagemodal(element.url),
                                        handleopenmodal()
                                      )}
                                    />
                              
                                </div>
                              );
                            })
                          : null}
                      </Form.Row>
                    </Modal.Body>
                  </Modal>
                  <Modalimage
                                        isopen={isopen}
                                        handleopenmodal={handleopenmodal}
                                        handleclosemodal={handleclosemodal}
                                        imagemodal={imagemodal}
                            />
                </Form.Row>
                <Form.Label>
                <div className="count-report">  
                       {reportelement.count} 
                </div>
                </Form.Label>
              </Form.Group>

              <Form.Group controlId="exampleForm.ControlTextarea1">
                <Form.Label></Form.Label>
                <div className="textarea-report"> 
                  <Form.Control
                    as="textarea"
                    rows={4}
                    readOnly={true}
                    value={reportelement.description}
                  />
                </div>
              </Form.Group>
            </Form.Row>
          </Form>

          <div className="container-reportsetiing d-sm-none">
              <div className="menu-containerreportsetting">
                <div
                  onClick={() => setIsActive(!isActive)}
                  className="reportbuttonsetting"
                >
                  <img
                    className="reportimg-setting"
                    src="/img/setting.png"
                    alt="avatar"
                  ></img>
                </div>
                <div
                  className={`reportmenusetting ${
                    isActive ? "active" : "inactive"
                  }`}
                >
                  <ul className="ul-reportmenusetting">
                    <li className="li-reportmenusetting">
                      <a className="a-reportmenusetting" onClick={() => cancleHide()}>
                        <div
                          className="a-reportmenusetting1"
                        >
                          
                        </div>
                      </a>
                    </li>
                    <li className="li-reportmenusetting">
                      <div
                        className="a-reportmenusetting"
                        onClick={() => deleteReport()}
                      >
                        {" "}
                        {" "}
                      </div>
                    </li>
                  </ul>
                </div>
              </div>
            </div>

          <div className="report-left d-sm-none">
              <Form.Group
                as={Col}
                className="col-md-6 col-12"
                controlId="formGridName"
              >
                <Form.Label>
                   :{" "}
                  {UsernameReport && UsernameReport[0].username}{" "}
                </Form.Label>
              </Form.Group>             
            </div>

            <div className="report-left d-sm-none">
              <Form.Group
                as={Col}
                className="col-md-6 col-12"
                controlId="formGridName"
              >
                <Form.Label>
                   :{" "}
                  {moment(new Date(reportelement.date.seconds * 1000)).format(
                    "MM/DD/YYYY HH:mm"
                  )}{" "}
                </Form.Label>
              </Form.Group>           
            </div>            

            <div className="report-left d-sm-none">
              <Form.Group
                as={Col}
                className="col-md-6 col-12"
                controlId="formGridName"
              >
                <Form.Label>
                   : {UsernamePost && UsernamePost[0].username}{" "}
                </Form.Label>
              </Form.Group>        
            </div>

            <div className="report-left d-sm-none">
              <Form.Group
                as={Col}
                className="col-md-6 col-12"
                controlId="formGridName"
              >
                <Form.Label>
                   :
                  <div
                    variant="primary"
                    onClick={(e) => handleShow(e)}
                    className="proof-button-reported"
                  >
                    
                  </div>
                </Form.Label>
                <Form.Row>
                  <Modal
                    show={Show}
                    onHide={handleClose}
                    className="modalreport"
                  >
                    <Modal.Header closeButton>
                      <Modal.Title className="namereport">
                        
                      </Modal.Title>
                    </Modal.Header>
                    <Modal.Body className="bigreport1">
                      <Form.Row>
                        {reportelement.fileUploads
                          ? reportelement.fileUploads.map((element, index) => {
                              return (
                                <div className="img-holder-badslip">
                                  <img
                                    className="img-badreport"
                                    alt=""
                                    src={`${element.url}`}
                                    style={{ overflow: "hidden" }}
                                    onClick={() => (
                                      Setimagemodal(element.url),
                                      handleopenmodal()
                                    )}
                                  />
                                </div>
                              );
                            })
                          : null}
                      </Form.Row>
                    </Modal.Body>
                  </Modal>
                  <Modalimage
                    isopen={isopen}
                    handleopenmodal={handleopenmodal}
                    handleclosemodal={handleclosemodal}
                    imagemodal={imagemodal}
                  />
                </Form.Row>
                <Form.Label>
                  <div className="count-report">
                     {reportelement.count} 
                  </div>
                </Form.Label>
              </Form.Group>      
            </div>             
                          
            <div className="report-right d-sm-none">
              {checkselectOne ? (
                <Form.Group controlId="formBasicCheckbox">
                  <Form.Check
                    type="checkbox"
                    label=""
                    checked
                    disabled
                  />
                </Form.Group>
              ) : (
                <Form.Group controlId="formBasicCheckbox">
                  <Form.Check
                    type="checkbox"
                    label=""
                    disabled
                  />
                </Form.Group>
              )}               
            </div>
            
            <div className="report-right d-sm-none">
              {checkselectTwo ? (
                <Form.Group controlId="formBasicCheckbox">
                  <Form.Check
                    type="checkbox"
                    label=""
                    checked
                    disabled
                  />
                </Form.Group>
              ) : (
                <Form.Group controlId="formBasicCheckbox">
                  <Form.Check
                    type="checkbox"
                    label=""
                    disabled
                  />
                </Form.Group>
              )}
            </div>

            <div className="report-right d-sm-none">
              {checkselectThree ? (
                <Form.Group controlId="formBasicCheckbox">
                  <Form.Check type="checkbox" label="" checked disabled />
                </Form.Group>
              ) : (
                <Form.Group controlId="formBasicCheckbox">
                  <Form.Check type="checkbox" label="" disabled />
                </Form.Group>
              )}
            </div>

            <div className="report-right d-sm-none">
              <Form.Row>
                <Form.Group controlId="exampleForm.ControlTextarea1">
                  <Form.Label></Form.Label>
                  <div className="textarea-report">
                    <Form.Control
                      as="textarea"
                      rows={4}
                      readOnly={true}
                      value={reportelement.description}
                    />
                  </div>
                </Form.Group>
              </Form.Row>
            </div>    

        </div>
      </div>
    </div>
  );
};

export default Listhidereport;
