        }
        if(other && doc.social.toLowerCase().includes(other.toLowerCase())){
          item.push(doc)
        }
       
        // if(!facebook){
        //   item.forEach(doc => {
        //     if(doc.social === "Facebook"){
        //       item.delete(doc)
        //     } 
       
            
        //   })
        // }
        
        })
 
   
    Setresult(item)
  
    }
     
    
     
      
    
