                  <div className="post-namegroup3"></div>
                  <div className="row post-numbergroup1">
                    <input
                      type="number"
                      id="nameproduct"
                      pattern="[0-9]{1,}"
                      className="postnumber1"
                    ></input>
                    <div className="post-numbergroup2">-</div>
                    <input
                      type="number"
                      id="nameproduct"
                      pattern="[0-9]{1,}"
                      className="postnumber2"
                    ></input>
                    <div className="postbuttonnumber">
